sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
		this.rightNow = new GlideDateTime();
		this.today = this.rightNow.getLocalDate();
		//gs.addInfoMessage("today : " + this.rightNow.getDate() );
    },
	isToday: function(strDate){
		var testDate = new GlideDateTime(strDate).getLocalDate();
		//var today = this.rightNow.getLocalDate();
		//gs.addInfoMessage("testDate : " + testDate );
		//gs.addInfoMessage("today : " + today );
		//gs.addInfoMessage("compareTo : " + (testDate.compareTo(today)) );
		//gs.addInfoMessage("is it today? : " + (testDate.compareTo(today) == 0) );
		// return (testDate.equals(today));
		return(testDate.compareTo(this.today) == 0);
	},
	isDatePast: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		//var rightNow = new GlideDateTime();		
		var testDate = new GlideDateTime(strDate).getLocalDate();

		// If the testdate is before rightnow, return true, else return false
		return (testDate.compareTo(this.today) == -1);
	},

    type: 'NeedItUtils'
};
